            });
            setTimeout(() => {
                var phoneCodeOnload = $("select[name=country] :selected").attr("data-mobile-code");
                placePhoneCode(phoneCodeOnload);
            }, 400);
            countrySelect(".country-select", $(".country-select").siblings(".select2"));
        });
    </script>
    <script>
        function selectContainItem(targetItem) {
            $(".select-account").slideUp(300);
            if (targetItem == null || targetItem === '') return;
            var findTargetItem = $(".register-doar-form-wrap .select-account");
            findTargetItem.each(function() {
                if ($(this).attr("data-select-target") == targetItem) {
                    $(this).slideDown(300);
                }
            });
        }

        $(document).ready(function() {
            var $step0 = $("#registerStep0");
            var $step1 = $("#registerStep1");
            var $step2 = $("#registerStep2");
            var $seg1 = $("#registerProgressSeg1"), $seg2 = $("#registerProgressSeg2"), $seg3 = $("#registerProgressSeg3");
            var $stepLabel = $("#registerStepLabel");
            var $accountType = $("#registerAccountType");
            var $cards = $(".register-doar-card");
            var hasStep2Visible = $step2.is(":visible");

            function setProgressStep(step) {
                $seg1.removeClass("register-doar-progress-seg--active");
                $seg2.removeClass("register-doar-progress-seg--active");
                $seg3.removeClass("register-doar-progress-seg--active");
                if (step >= 1) $seg1.addClass("register-doar-progress-seg--active");
                if (step >= 2) $seg2.addClass("register-doar-progress-seg--active");
                if (step >= 3) $seg3.addClass("register-doar-progress-seg--active");
                $(".register-doar-progress").attr("aria-valuenow", step);
            }

            if (hasStep2Visible) {
                $step0.hide();
                $step1.hide();
                $step2.show();
                var type = $accountType.val();
                if (type === "1") {
                    $stepLabel.text("{{ __('Paso 3 de 3') }}");
                    setProgressStep(3);
                    initOrUpdateRegisterSliderForm();
                } else {
                    $stepLabel.text("{{ __('Paso 2 de 3') }}");
                    setProgressStep(2);
                }
                $cards.removeClass("register-doar-card--selected");
                $cards.find(".register-doar-card-btn").removeClass("register-doar-card-btn--primary");
                $cards.filter("[data-account-type=" + type + "]").addClass("register-doar-card--selected").find(".register-doar-card-btn").addClass("register-doar-card-btn--primary");
            } else {
                $step0.hide();
                $step1.show();
                $step2.hide();
                $stepLabel.text("{{ __('Paso 1 de 3') }}");
                setProgressStep(1);
            }

            // Volver desde paso redes sociales → paso 1 (tipo de cuenta)
            $("#registerBackToStep0").on("click", function(e) {
                e.preventDefault();
                $step0.hide();
                $step1.show();
                $step2.hide();
                $stepLabel.text("{{ __('Paso 1 de 3') }}");
                setProgressStep(1);
            });

            // Selección visual de card (estilo checkbox)
            $cards.on("click", function(e) {
                if ($(e.target).closest(".register-doar-card-btn").length) return;
                var type = $(this).data("account-type");
                $cards.removeClass("register-doar-card--selected");
                $cards.find(".register-doar-card-btn").removeClass("register-doar-card-btn--primary");
                $(this).addClass("register-doar-card--selected");
                $(this).find(".register-doar-card-btn").addClass("register-doar-card-btn--primary");
            });

            // "Abrir ahora" Personal (1) → paso redes sociales; Empresa (2) → formulario
            $(".register-doar-card-btn[data-open-form]").on("click", function(e) {
                e.preventDefault();
                var type = $(this).data("open-form").toString();
                $accountType.val(type);
                selectContainItem(type);
                if (type === "1") {
                    $step0.show();
                    $step1.hide();
                    $step2.hide();
                    $stepLabel.text("{{ __('Paso 2 de 3') }}");
                    setProgressStep(2);
                    initOrUpdateRegisterSlider();
                } else {
                    $step0.hide();
                    $step1.hide();
                    $step2.show();
                    $stepLabel.text("{{ __('Paso 2 de 3') }}");
                    setProgressStep(2);
                }
            });

            // "Registrarme con mi correo" (desde paso redes) → formulario personal
            $("#registerGoToEmail").on("click", function(e) {
                e.preventDefault();
                $accountType.val("1");
                selectContainItem("1");
                $step0.hide();
                $step1.hide();
                $step2.show();
                $stepLabel.text("{{ __('Paso 3 de 3') }}");
                setProgressStep(3);
                initOrUpdateRegisterSliderForm();
            });

            // Volver desde formulario: Personal → paso redes; Empresa → paso tipo cuenta
            $("#registerBackFromForm").on("click", function(e) {
                e.preventDefault();
                var type = $accountType.val();
                if (type === "1") {
                    $step0.show();
                    $step1.hide();
                    $step2.hide();
                    $stepLabel.text("{{ __('Paso 2 de 3') }}");
                    setProgressStep(2);
                    initOrUpdateRegisterSlider();
                    } else {
                    $step0.hide();
                    $step1.show();
                    $step2.hide();
                    $stepLabel.text("{{ __('Paso 1 de 3') }}");
                    setProgressStep(1);
                }
            });

            // Slider paso 0 (redes sociales): inicializar cuando el paso esté visible
            var registerDoarSwiper = null;
            function initOrUpdateRegisterSlider() {
                if (typeof Swiper === "undefined") return;
                var el = document.querySelector("#registerStep0 .register-doar-slider");
                if (!el || $("#registerStep0").is(":hidden")) return;
                if (registerDoarSwiper) {
                    setTimeout(function() { registerDoarSwiper.update(); }, 50);
                    return;
                }
                setTimeout(function() {
                    if (registerDoarSwiper) return;
                    registerDoarSwiper = new Swiper("#registerStep0 .register-doar-slider", {
                        slidesPerView: 1,
                        spaceBetween: 16,
                        loop: true,
                        autoplay: { delay: 4500, disableOnInteraction: false },
                        pagination: { el: "#registerStep0 .register-doar-slider-pagination", clickable: true }
                    });
                }, 50);
            }

            // Slider paso 2 (formulario personal): inicializar cuando el formulario personal esté visible
            var registerDoarSwiperForm = null;
            function initOrUpdateRegisterSliderForm() {
                if (typeof Swiper === "undefined") return;
                var el = document.querySelector("#registerStep2 .register-doar-slider");
                if (!el || $("#registerStep2").is(":hidden")) return;
                if (registerDoarSwiperForm) {
                    setTimeout(function() { registerDoarSwiperForm.update(); }, 50);
                    return;
                }
                setTimeout(function() {
                    if (registerDoarSwiperForm) return;
                    registerDoarSwiperForm = new Swiper("#registerStep2 .register-doar-slider", {
                        slidesPerView: 1,
                        spaceBetween: 16,
                        loop: true,
                        autoplay: { delay: 4500, disableOnInteraction: false },
                        pagination: { el: "#registerStep2 .register-doar-slider-pagination", clickable: true }
                    });
                }, 50);
            }
        });
    </script>
@endpush
